    }
  }

  # Create histogram data
  if(length(GroupVar) == 0L) {
    Min <- dt1[, min(get(XVar), na.rm = TRUE)]
    Max <- dt1[, max(get(XVar), na.rm = TRUE)]
    Range <- Max - Min
