      acc <- round(Range / NumberBins, 2)
      dt1[, Buckets := round(get(XVar) / acc) * acc]
      dt1 <- dt1[, .N, by = "Buckets"][order(Buckets)]
    } else {
      acc <- ceiling(Range / NumberBins)
