                      title.subtextStyle.textShadowOffsetX = NULL,
                      title.subtextStyle.textShadowOffsetY = NULL,
                      Debug = FALSE) {

  if(Theme == 'auritus') {
    ColorVals <- c("#3e4359", "#c5a805", "#4d267e", "#22904f", "red")
