    YVarTrans = YVarTrans,
    XVarTrans = XVarTrans,
    FacetRows = 1,
    FacetCols = 1,
    FacetLevels = NULL,
    ShowLabels = ShowLabels,
    Height = Height,
    Width = Width,
    MouseScroll = MouseScroll,
    Theme = Theme,
    TimeLine = tl,
    TextColor = TextColor,
    Debug = Debug)
  return(p1)
}

